
SET MODE Oracle;

-- Não precisa na versão 1.4 do h2 (usando a 1.3 por causa do eap 6)
drop alias if exists TO_DATE;
create alias TO_DATE as $$
java.util.Date toDate(String s, String format) throws Exception {
    return new java.text.SimpleDateFormat("yy/MM/dd").parse(s);
}
$$;

